   */
  @Input()
  label = 'Checkbox label';

  /**
   * Optional click handler
   */
  @Output()
  onClick = new EventEmitter < Event > ();


  public get classes(): string[] {

    let story;
    switch (this.type) {
      case 'normal':
        story = 'storybook-checkbox--normal'
        break;
      case 'indeterminate':
        story = 'storybook-checkbox--indeterminate'
        break;
      case 'disabled':
